mapper.insert:
SELECT ident_current('INTE_ALERT') AS serialNo
INSERT dbo.INTE_ALERT(
) VALUES ( #{alertCode,jdbcType=VARCHAR}, #{alertName,jdbcType=VARCHAR}, #{alertTheme,jdbcType=VARCHAR}, #{msgSequenceId,jdbcType=INTEGER}, #{alertContent,jdbcType=VARCHAR}, #{alertLevel,jdbcType=VARCHAR}, #{alertStatus,jdbcType=VARCHAR}, GETDATE(), #{notifyPerson,jdbcType=VARCHAR}, #{alertMemo,jdbcType=VARCHAR} )

mapper.select:
select * from INTE_ALERT where 1=1

	and ALERT_CODE=#{alertCode,jdbcType=VARCHAR}


	and ALERT_NAME=#{alertName,jdbcType=VARCHAR}


	and ALERT_THEME=#{alertTheme,jdbcType=VARCHAR}


	and MSG_SEQ_ID=#{msgSequenceId,jdbcType=INTEGER}


	and ALERT_LEVEL=#{alertLevel,jdbcType=VARCHAR}


	and ALERT_STATUS=#{alertStatus,jdbcType=VARCHAR}


	and NOTIFY_PERSON=#{notifyPerson,jdbcType=VARCHAR}


	and CREATE_DATE  >=  CONVERT(DATETIME,#{startDate,jdbcType=TIMESTAMP},120)


	and CREATE_DATE  <=  CONVERT(DATETIME,#{endDate,jdbcType=TIMESTAMP},120)

 order by SERIAL_NO desc
