mapper.select:
SELECT
*
FROM
MSG_EVENT_TYPE 
WHERE 
MESSAGE_TYPE=#{mesageType,jdbcType=VARCHAR}
and EVENT_TYPE=#{eventType,jdbcType=VARCHAR}

	and CONTROL_CODE=#{controlCode,jdbcType=VARCHAR}

and TRANS_CODE=#{transCode,jdbcType=VARCHAR}

mapper.insert:
SELECT ident_current('MSG_EVENT_TYPE') AS typeId
INSERT INTO MSG_EVENT_TYPE (
) VALUES ( #{mesageType,jdbcType=VARCHAR}, #{eventType,jdbcType=VARCHAR}, #{controlCode,jdbcType=VARCHAR}, #{transCode,jdbcType=VARCHAR}, #{messageTypeDesc,jdbcType=VARCHAR}, #{createDate,jdbcType=TIMESTAMP} )

mapper.select:
select * from MSG_EVENT_TYPE
where 1=1

	and MESSAGE_TYPE=#{mesageType,jdbcType=VARCHAR}


	and EVENT_TYPE=#{eventType,jdbcType=VARCHAR}


	and CONTROL_CODE=#{controlCode,jdbcType=VARCHAR}


	and TRANS_CODE=#{transCode,jdbcType=VARCHAR}
